export var DEFAULT_OFFLINE_IMAGE_URL = '/offline.png';
export var DEFAULT_PRIVATE_IMAGE_URL = '/private.png';
export var DEFAULT_GROUP_IMAGE_URL = '/group.png';
export var DEFAULT_ONLINE_IMAGE_URL = '';
export function getPoster(status) {
  var poster = '';

  switch (status) {
    case 'private':
      poster = DEFAULT_PRIVATE_IMAGE_URL;
      break;

    case 'offline':
      poster = DEFAULT_OFFLINE_IMAGE_URL;
      break;

    case 'public':
      poster = DEFAULT_ONLINE_IMAGE_URL;
      break;

    case 'group':
      poster = DEFAULT_GROUP_IMAGE_URL;
      break;

    default:
      poster = DEFAULT_OFFLINE_IMAGE_URL;
      break;
  }

  return poster;
}
