import { message } from 'antd';
export function beforeAvatarUpload(file) {
  var ext = file.name.split('.').pop().toLowerCase();
  var isImageAccept = process.env.NEXT_PUBLIC_IMAGE_ACCPET.split(',').map(function (item) {
    return item.trim();
  }).indexOf(".".concat(ext));

  if (isImageAccept === -1) {
    message.error("You can only upload ".concat(process.env.NEXT_PUBLIC_IMAGE_ACCPET, " file!"));
    return false;
  }

  var isLt2M = file.size / 1024 / 1024 < (process.env.NEXT_PUBLIC_MAXIMUM_SIZE_UPLOAD_AVATAR || 2);

  if (!isLt2M) {
    message.error("Image must smaller than ".concat(process.env.NEXT_PUBLIC_MAXIMUM_SIZE_UPLOAD_AVATAR || 2, "MB!"));
    return false;
  }

  return true;
}
