import moment from 'moment';
export function formatDate(date, format = 'DD/MM/YYYY HH:mm:ss') {
  return moment(date).format(format);
}
export function converDuration(duration, format = 'HH:mm') {
  return moment.utc(moment.duration(duration, 'milliseconds').asMilliseconds()).format(format);
}
export function parseAge(date, format = 'MMMM DD, YYYY') {
  return moment(date).format(format);
}
export function getAge(d) {
  if (!d) return '';
  const age = new Date().getFullYear() - new Date(d).getFullYear();
  return age;
}
export function formatDuration(s) {
  const hours = Math.floor(s / 3600);
  const minutes = Math.floor((s - hours * 3600) / 60);
  const seconds = s - hours * 3600 - minutes * 60;
  return `${hours < 10 ? '0' : ''}${hours}:${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
}
